COMMIT;

CREATE TABLE A4GT_CONTROLLI_LATTE( ID ${id.number.type} NOT NULL, VERSION ${version.number.type} NOT NULL, CUAA ${string.type}(16) NOT NULL, ANNO_RIFERIMENTO NUMBER(4) NOT NULL, DATA_CONSEGNA_O_VENDITA DATE, CELLULE_SOMATICHE NUMBER(10,2), CARICA_BATTERICA NUMBER(10,2), CONTENUTO_PROTEINA NUMBER(10,2), DATA_ANALISI DATE, DATA_INIZIO_ALPEGGIO DATE, DATA_FINE_ALPEGGIO DATE,
CONSTRAINT PK_CONTROLLI_LATTE PRIMARY KEY (ID) ) TABLESPACE ${tablespace.data};
CREATE UNIQUE INDEX A4GT_CONTROLLI_LATTE_UIDX ON A4GT_CONTROLLI_LATTE (CUAA,ANNO_RIFERIMENTO) TABLESPACE ${tablespace.index};
ALTER INDEX PK_CONTROLLI_LATTE REBUILD TABLESPACE ${tablespace.index} ONLINE;
GRANT SELECT,INSERT,UPDATE,DELETE ON A4GT_CONTROLLI_LATTE TO ${user.writer};

